    void hover(int so, JPanel pnl) {
        if (so == 1) {
            pnl.setBackground(new Color(153, 0, 153));
            pnl.setCursor(new Cursor(HAND_CURSOR));
        } else {
            pnl.setBackground(new Color(102, 0, 102));
        }
    }

    void showHideSubMenu(int so, JPanel panel) {
        so++;
        if (so % 2 == 0) {
            pnlsubmenu.setVisible(true);
        } else {
            pnlsubmenu.setVisible(false);
        }
        JPanel[] pnl = new JPanel[]{pnlsub_thongke, pnlsub_hotro};
        for (int i = 0; i < pnl.length; i++) {
            pnl[i].setVisible(false);
        }
        panel.setVisible(true);
    }

    void minus() {
        this.setState(1);
    }

    void setcolor(JPanel pnl) {
        pnl.setBackground(new Color(153, 0, 153));

    }

    void resetcolor(JPanel pnl) {
        pnl.setBackground(new Color(102, 0, 102));
    }
    void author(){
        if (ShareHelper.USER != null) {
            lblTen.setText(ShareHelper.USER.getHoTen());
            lblChucvu.setText(ShareHelper.USER.getVaiTro() ? "Trưởng Phòng":"Nhân Viên" );
        }else{
            lblTen.setText("");
            lblChucvu.setText("");
        }
       
        
    }

